SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_atualizar_subestipul () AS $body$
DECLARE


nr_seq_beneficiario_w		bigint;
nr_seq_contrato_w		bigint;
cd_subestipulante_pf_w		varchar(10);
cd_subestipulante_pj_w		varchar(14);
nr_seq_subestipulante_w		bigint	:= 0;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_contrato,
		cd_subestipulante_pf,
		cd_subestipulante_pj
	from	pls_segurado
	where	(cd_subestipulante_pf IS NOT NULL AND cd_subestipulante_pf::text <> '') or (cd_subestipulante_pj IS NOT NULL AND cd_subestipulante_pj::text <> '')
	and	coalesce(nr_seq_subestipulante::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_beneficiario_w,
	nr_seq_contrato_w,
	cd_subestipulante_pf_w,
	cd_subestipulante_pj_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (cd_subestipulante_pf_w IS NOT NULL AND cd_subestipulante_pf_w::text <> '') then
		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_subestipulante_w
		from	pls_sub_estipulante
		where	nr_seq_contrato		= nr_seq_contrato_w
		and	cd_pessoa_fisica	= cd_subestipulante_pf_w;
	elsif (cd_subestipulante_pj_w IS NOT NULL AND cd_subestipulante_pj_w::text <> '') then
		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_subestipulante_w
		from	pls_sub_estipulante
		where	nr_seq_contrato	= nr_seq_contrato_w
		and	cd_cgc		= cd_subestipulante_pj_w;
	end if;

	if (nr_seq_subestipulante_w > 0) then
		update	pls_segurado
		set	nr_seq_subestipulante	= nr_seq_subestipulante_w
		where	nr_sequencia		= nr_seq_beneficiario_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
