CREATE OR REPLACE PROCEDURE baca_pls_atualizar_usuario_aud () AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
nr_seq_auditor_w		bigint;
nm_usuario_exec_w		varchar(15);

C01 CURSOR FOR
	SELECT	distinct cd_pessoa_fisica
	from	pls_membro_grupo_aud
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select 	coalesce(max(nm_usuario),'')
	into STRICT	nm_usuario_exec_w
	from	usuario
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w;

	update	pls_membro_grupo_aud
	set	nm_usuario_exec		= nm_usuario_exec_w
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w;

	end;
end loop;
close C01;

commit;

end;
$body$
