CREATE OR REPLACE PROCEDURE baca_pls_carteirinha_aciplan () AS $body$
DECLARE


nr_seq_contrato_w		bigint;
nr_seq_seg_contrato_w		bigint;
cd_usuario_plano_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_contrato;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	coalesce(max(nr_seq_seg_contrato),1)
	into STRICT	nr_seq_seg_contrato_w
	from	pls_segurado
	where	nr_seq_contrato	= nr_seq_contrato_w;

	select	max((cd_usuario_plano)::numeric )
	into STRICT	cd_usuario_plano_w
	from (SELECT	substr(a.cd_usuario_plano,length(cd_usuario_plano)-4,5) cd_usuario_plano
		from	pls_segurado		b,
			pls_segurado_carteira	a
		where	a.nr_seq_segurado	= b.nr_sequencia
		and	b.nr_seq_contrato	= nr_seq_contrato_w) alias4;

	update	pls_segurado
	set	nr_seq_seg_contrato	= cd_usuario_plano_w + 1
	where	nr_seq_contrato		= nr_seq_contrato_w
	and	nr_seq_seg_contrato	= nr_seq_seg_contrato_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
