CREATE OR REPLACE PROCEDURE baca_pls_composicao_pacote ( nm_usuario_p text ) AS $body$
DECLARE

 
ds_composicao_w		varchar(120);
nr_seq_pacote_w		bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
nr_sequencia_w		bigint;

C01 CURSOR FOR 
	SELECT 	nr_sequencia, 
		cd_procedimento, 
		ie_origem_proced 
	from 	pls_pacote;

BEGIN
 
open C01;
loop  
fetch C01 into	 
	nr_seq_pacote_w, 
	cd_procedimento_w, 
	ie_origem_proced_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	ds_composicao_w	:= obter_descricao_procedimento(cd_procedimento_w, ie_origem_proced_w);
	 
	select	nextval('pls_pacote_composicao_seq') 
	into STRICT 	nr_sequencia_w 
	;
	 
	insert into pls_pacote_composicao(nr_sequencia, ie_situacao, ds_composicao, 
		 dt_atualizacao, nm_usuario, nr_seq_pacote) 
	values (nr_sequencia_w, 'A', ds_composicao_w, 
		 clock_timestamp(), nm_usuario_p, nr_seq_pacote_w);
		 
	update	pls_pacote_procedimento 
	set 	nr_seq_composicao = nr_sequencia_w 
	where	nr_seq_pacote 	= nr_seq_pacote_w;
	 
	update	pls_pacote_material 
	set 	nr_seq_composicao = nr_sequencia_w 
	where 	nr_seq_pacote = nr_seq_pacote_w;
 
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
