SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_comunic_externa_web () AS $body$
DECLARE

 
tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
tb_tipo_w		pls_util_cta_pck.t_number_table;
tb_tipo_comnicado_w	pls_util_cta_pck.t_varchar2_table_2;

c01 CURSOR FOR 
	SELECT	nr_sequencia, 
		1 ie_tipo, 
		'1' tipo_comunicado 	--Inclusão 
	from	pls_comunic_externa_web 
	where	ds_titulo = 'Reinclusão de beneficiário rejeitada' 
	
union all
 
	SELECT	nr_sequencia, 
		2 ie_tipo, 
		'2' tipo_comunicado 	--Exclusão 
	from	pls_comunic_externa_web 
	where	ds_titulo = 'Rescisão de beneficiário rejeitada' 
	
union all
 
	select	nr_sequencia, 
		3 ie_tipo, 
		'1' tipo_comunicado 	--Inclusão 
	from	pls_comunic_externa_web 
	where	ds_titulo = 'Inclusão rejeitada' 
	
union all
 
	select	nr_sequencia, 
		4 ie_tipo, 
		'3' tipo_comunicado 	--Alteração 
	from	pls_comunic_externa_web 
	where	ds_titulo = 'Alteração de Produto rejeitada' 
	
union all
 
	select	nr_sequencia, 
		5 ie_tipo, 
		'3' tipo_comunicado 	--Alteração 
	from	pls_comunic_externa_web 
	where	ds_titulo = 'Solicitação de 2ª via de carteirinha rejeitada' 
	
union all
 
	select	nr_sequencia, 
		6 ie_tipo, 
		'3' tipo_comunicado 	--Alteração 
	from	pls_comunic_externa_web 
	where	ds_titulo = 'Alteração de dados cadastrais rejeitada' 
	order by ie_tipo;
	
/* 
 Popular campo ie_tipo_comunicado 
 
3        Alteração 
2        Exclusão 
1        Inclusão 
 
*/
 

BEGIN 
 
open C01;
loop 
	fetch C01 bulk collect into tb_nr_sequencia_w, tb_tipo_w, tb_tipo_comnicado_w 
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;
 
	forall	i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last 
		update	pls_comunic_externa_web 
		set	ie_tipo_comunicado = tb_tipo_comnicado_w(i) 
		where 	nr_sequencia = tb_nr_sequencia_w(i);
 
	commit;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_pls_comunic_externa_web () FROM PUBLIC;

