SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_contr_lote_rev_pess () AS $body$
DECLARE


nr_seq_contrato_w		bigint;
nr_contrato_w			bigint;
nr_seq_lote_rev_pessoa_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_contrato
	from	pls_lote_pessoa_inconsist
	where	coalesce(nr_contrato::text, '') = '';


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_lote_rev_pessoa_w,
	nr_seq_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(nr_contrato)
	into STRICT	nr_contrato_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_w;

	if (nr_contrato_w IS NOT NULL AND nr_contrato_w::text <> '') then
		update	pls_lote_pessoa_inconsist
		set	nr_contrato	= nr_contrato_w
		where	nr_sequencia	= nr_seq_lote_rev_pessoa_w
		and	coalesce(nr_contrato::text, '') = '';
	end if;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
