CREATE OR REPLACE PROCEDURE baca_pls_lib_plano_proposta ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_plano_w			bigint;
qt_registro_w			integer	:= 0;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_plano
	where	ie_proposta_adesao = 'S';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_plano_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	pls_plano_lib_proposta
	where	nr_seq_plano	= nr_seq_plano_w;

	if (qt_registro_w = 0) then
		insert into pls_plano_lib_proposta(nr_sequencia, nr_seq_plano, dt_inicio_vigencia,
			dt_liberacao, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec, dt_fim_vigencia,
			nm_usuario_liberacao, ds_observacao)
		values (	nextval('pls_plano_lib_proposta_seq'), nr_seq_plano_w, clock_timestamp(),
			clock_timestamp(), clock_timestamp(),  'BacaTasy',
			clock_timestamp(), 'BacaTasy', null,
			'BacaTasy', 'gerado pelo sistema');
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
