SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_lote_mens () AS $body$
DECLARE


nr_seq_lote_w		bigint;
vl_pro_rata_dia_w	double precision;
vl_antecipacao_w	double precision;
qt_pagadores_w		bigint;
qt_beneficiario_lote_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_lote_mensalidade;

BEGIN

open C01;
loop
fetch C01 into
	nr_seq_lote_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	coalesce(sum(a.vl_pro_rata_dia),0),
		coalesce(sum(a.vl_antecipacao),0)
	into STRICT	vl_pro_rata_dia_w,
		vl_antecipacao_w
	from	pls_mensalidade_seg_item	a,
		pls_mensalidade_segurado	b,
		pls_mensalidade			c,
		pls_lote_mensalidade		d
	where	b.nr_seq_mensalidade		= c.nr_sequencia
	and	d.nr_sequencia			= nr_seq_lote_w
	and	a.nr_seq_mensalidade_seg	= b.nr_sequencia
	and	c.nr_seq_lote			= d.nr_sequencia;

	select	count(*)
	into STRICT	qt_beneficiario_lote_w
	from	pls_mensalidade_segurado	a,
		pls_mensalidade			b,
		pls_lote_mensalidade		c
	where	b.nr_seq_lote = c.nr_sequencia
	and	a.nr_seq_mensalidade = b.nr_sequencia
	and	b.nr_seq_lote = nr_seq_lote_w
	and	coalesce(b.ie_cancelamento::text, '') = '';

	select	count(*)
	into STRICT	qt_pagadores_w
	from	pls_mensalidade
	where	nr_seq_lote = nr_seq_lote_w
	and	coalesce(ie_cancelamento::text, '') = '';

	update	pls_lote_mensalidade
	set	vl_pro_rata_dia		= vl_pro_rata_dia_w,
		vl_antecipacao		= vl_antecipacao_w,
		qt_pagadores_lote	= qt_pagadores_w,
		qt_beneficiario_lote	= qt_beneficiario_lote_w
	where	nr_sequencia	= nr_seq_lote_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
