SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_parametros_importacao () AS $body$
DECLARE


ie_material_rede_propria_w	varchar(10);
ie_importacao_material_w	varchar(10);
ie_material_ops_w		varchar(10);
cd_estabelecimento_w		bigint;
qt_reg_w			bigint;

c01 CURSOR FOR
	SELECT	ie_material_rede_propria,
		ie_importacao_material,
		ie_material_ops,
		cd_estabelecimento
	from	pls_parametros;


BEGIN
begin
open C01;
loop
fetch C01 into
	ie_material_rede_propria_w,
	ie_importacao_material_w,
	ie_material_ops_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_reg_w
	from	pls_param_importacao_conta
	where	cd_estabelecimento		= cd_estabelecimento_w;

	if (qt_reg_w > 0) then
		update	pls_param_importacao_conta
		set	ie_material_rede_propria	= ie_material_rede_propria_w,
			ie_importacao_material		= ie_importacao_material_w,
			ie_material_ops			= ie_material_ops_w
		where	cd_estabelecimento		= cd_estabelecimento_w;
	else
		insert into pls_param_importacao_conta(nr_sequencia, dt_atualizacao, nm_usuario,
			 dt_atualizacao_nrec, nm_usuario_nrec, ie_material_rede_propria,
			 ie_importacao_material, ie_material_ops, cd_estabelecimento)
		values (nextval('pls_param_importacao_conta_seq'), clock_timestamp(), 'tasy',
			 clock_timestamp(), 'tasy', ie_material_rede_propria_w,
			 ie_importacao_material_w, ie_material_ops_w, cd_estabelecimento_w);

	end if;

	end;
end loop;
close C01;
exception
when others then
	cd_estabelecimento_w := cd_estabelecimento_w;
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
