CREATE OR REPLACE PROCEDURE baca_pls_regra_importacao_mat () AS $body$
DECLARE


ie_tipo_material_w		varchar(10);
ie_material_ops_w		varchar(10);
ie_material_rede_propria_w	varchar(10);
cd_estabelecimento_w		bigint;
qt_registros_w			bigint;
C01 CURSOR FOR
	SELECT	coalesce(ie_material_ops,'N'),
		cd_estabelecimento,
		ie_material_rede_propria
	from	pls_param_importacao_conta;


BEGIN

open C01;
loop
fetch C01 into
	ie_material_ops_w,
	cd_estabelecimento_w,
	ie_material_rede_propria_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_tipo_material_w	:= 'S';

	if (ie_material_rede_propria_w	= 'S') then
		ie_tipo_material_w	:= 'H';
	elsif (ie_material_ops_w	= 'S') then
		ie_tipo_material_w	:= 'C';
	end if;

	update	pls_param_importacao_conta
	set	ie_tipo_material	= ie_tipo_material_w
	where	cd_estabelecimento	= cd_estabelecimento_w;

	end;
end loop;
close C01;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
