SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_rescisao_pagador ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_pagador_w		bigint;
qt_beneficiarios_w		bigint	:= 0;
qt_benef_inativos_w		bigint	:= 0;
dt_rescisao_w			timestamp;
nr_seq_motivo_cancelamento_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_contrato_pagador
	where	coalesce(dt_rescisao::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_pagador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_beneficiarios_w	:= 0;
	qt_benef_inativos_w	:= 0;

	select	count(*)
	into STRICT	qt_beneficiarios_w
	from	pls_segurado
	where	nr_seq_pagador	= nr_seq_pagador_w;

	select	count(*)
	into STRICT	qt_benef_inativos_w
	from	pls_segurado
	where	nr_seq_pagador	= nr_seq_pagador_w
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	(dt_rescisao IS NOT NULL AND dt_rescisao::text <> '');

	if (qt_beneficiarios_w > 0) and (qt_benef_inativos_w = qt_beneficiarios_w) then
		select	max(dt_rescisao),
			max(nr_seq_motivo_cancelamento)
		into STRICT	dt_rescisao_w,
			nr_seq_motivo_cancelamento_w
		from	pls_segurado
		where	nr_seq_pagador	= nr_seq_pagador_w;

		update	pls_contrato_pagador
		set	dt_rescisao			= dt_rescisao_w,
			nm_usuario			= 'baca',
			dt_atualizacao			= clock_timestamp(),
			nr_seq_motivo_cancelamento	= nr_seq_motivo_cancelamento_w
		where	nr_sequencia			= nr_seq_pagador_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
