SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_tipo_guia_analise () AS $body$
DECLARE


ie_tipo_guia_w			varchar(2);
qt_guias_w			bigint;
nr_seq_analise_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_analise_conta
	where	coalesce(ie_tipo_guia::text, '') = ''
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_analise_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ie_tipo_guia_w	:= '';


	select 	count(*)
	into STRICT	qt_guias_w
	from   	pls_analise_conta a,
		pls_conta b
	where  	b.nr_seq_analise = a.nr_sequencia
	and	b.ie_tipo_guia in ('5', '6')
	and    	a.nr_sequencia =  nr_seq_analise_w;

	if (qt_guias_w > 0) then
		ie_tipo_guia_w	:= '5';
	else

		select 	count(*)
		into STRICT	qt_guias_w
		from   	pls_analise_conta a,
			pls_conta b
		where  	b.nr_seq_analise = a.nr_sequencia
		and	b.ie_tipo_guia = '3'
		and    	b.nr_seq_analise =  nr_seq_analise_w;

		if (qt_guias_w > 0) then
			ie_tipo_guia_w	:= '3';
		else
			select 	count(*)
			into STRICT	qt_guias_w
			from   	pls_analise_conta a,
				pls_conta b
			where  	b.nr_seq_analise = a.nr_sequencia
			and	b.ie_tipo_guia = '4'
			and    	b.nr_seq_analise =  nr_seq_analise_w;

			if (qt_guias_w > 0) then
				ie_tipo_guia_w	:= '4';
			end if;
		end if;
	end if;

	update	pls_analise_conta
	set	ie_tipo_guia	= ie_tipo_guia_w
	where	nr_sequencia	= nr_seq_analise_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_pls_tipo_guia_analise () FROM PUBLIC;

