CREATE OR REPLACE PROCEDURE baca_pls_tipo_guia_reembolso ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_reembolso_w		bigint;
ie_tipo_atendimento_w		varchar(1);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_tipo_atendimento
	from	pls_reembolso
	where	(ie_tipo_atendimento IS NOT NULL AND ie_tipo_atendimento::text <> '')
	and	coalesce(ie_tipo_guia::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_reembolso_w,
	ie_tipo_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	pls_reembolso
	set	ie_tipo_guia	= CASE WHEN ie_tipo_atendimento_w='E' THEN '4' WHEN ie_tipo_atendimento_w='I' THEN '5' END
	where	nr_sequencia	= nr_seq_reembolso_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
