SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_prescr_medica_estab () AS $body$
DECLARE


i			integer;
qt_reg_w		bigint;



BEGIN

select count(*)
into STRICT	qt_reg_w
from 	prescr_medica
where	coalesce(cd_estabelecimento::text, '') = '';

qt_reg_w	:= qt_reg_w / 1000;
qt_reg_w	:= trunc(qt_reg_w) + 2;

FOR	i in 1 .. qt_reg_w LOOP
	update prescr_medica a
	set	cd_estabelecimento	= (
		SELECT cd_estabelecimento
		from	atendimento_paciente b
		where	a.nr_atendimento	= b.nr_atendimento)
	where	coalesce(a.cd_estabelecimento::text, '') = ''  LIMIT 999;

	commit;
END LOOP;

update prescr_medica a
set	cd_estabelecimento	= (
	SELECT min(cd_estabelecimento)
	from	estabelecimento)
where	coalesce(a.cd_estabelecimento::text, '') = ''
and	coalesce(nr_atendimento::text, '') = '';
commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
