CREATE OR REPLACE PROCEDURE baca_pre_fatur_estab () AS $body$
DECLARE


i	integer;


BEGIN

FOR	i in 1 .. 5 LOOP
	update conta_paciente a
	set	cd_estabelecimento	= (
		SELECT cd_estabelecimento
		from	atendimento_paciente b
		where	a.nr_atendimento	= b.nr_atendimento)
	where	coalesce(a.cd_estabelecimento::text, '') = ''  LIMIT 9999;
	commit;
END LOOP;

FOR	i in 1	.. 10 LOOP
	update pre_fatur_conta a
	set	cd_estabelecimento	= (
		SELECT cd_estabelecimento
		from	conta_paciente b
		where	a.nr_interno_conta	= b.nr_interno_conta)
	where	coalesce(a.cd_estabelecimento::text, '') = ''  LIMIT 9999;
	commit;
END LOOP;



FOR	i in 1 .. 15 LOOP
	update	pre_faturamento a
	set	cd_estabelecimento	=
		(SELECT cd_estabelecimento_base
		from setor_atendimento b
		where 	a.cd_setor_atendimento = b.cd_setor_atendimento)
	where	coalesce(a.cd_estabelecimento::text, '') = ''  LIMIT 29999;
	commit;
END LOOP;

END;
$body$
