SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_proj_atualizar_aprov_fin () AS $body$
DECLARE

 
nr_seq_rat_w			bigint;
nr_ordem_compra_w		bigint;
dt_aprovacao_w			timestamp;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		coalesce(nr_ordem_compra,0), 
		dt_aprovacao 
	from	proj_rat 
	where	(dt_aprov_fin IS NOT NULL AND dt_aprov_fin::text <> '');


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_rat_w, 
	nr_ordem_compra_w, 
	dt_aprovacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (nr_ordem_compra_w > 0) then 
		select	dt_ordem_compra 
		into STRICT	dt_aprovacao_w 
		from	ordem_compra 
		where	nr_ordem_compra	= nr_ordem_compra_w;
	end if;
	 
	if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then 
		update	proj_rat 
		set	dt_aprov_fin	= dt_aprovacao_w 
		where	nr_sequencia	= nr_seq_rat_w;
	end if;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
