CREATE OR REPLACE PROCEDURE baca_proj_atualiza_hora_real ( nr_seq_cronograma_p bigint) AS $body$
DECLARE


nr_seq_proj_w			bigint;
nr_seq_proj_cron_etapa_w	bigint;
nr_seq_ordem_w			bigint;
nr_seq_ordem_ativ_w		bigint;

C01 CURSOR FOR
	SELECT	b.nr_seq_proj_cron_etapa,
		b.nr_sequencia,
		c.nr_sequencia
	from	man_ordem_serv_ativ	c,
		man_ordem_servico	b,
		proj_ordem_servico	a
	where	a.nr_seq_proj		= nr_seq_proj_w
	and	a.nr_seq_ordem		= b.nr_sequencia
	and	b.nr_sequencia		= c.nr_seq_ordem_serv
	and	(b.nr_seq_proj_cron_etapa IS NOT NULL AND b.nr_seq_proj_cron_etapa::text <> '');


BEGIN

select	max(nr_seq_proj)
into STRICT	nr_seq_proj_w
from	proj_cronograma
where	nr_sequencia	= nr_seq_cronograma_p;

open C01;
loop
fetch C01 into
	nr_seq_proj_cron_etapa_w,
	nr_seq_ordem_w,
	nr_seq_ordem_ativ_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	man_ordem_serv_ativ
	set	nr_seq_proj_cron_etapa	= nr_seq_proj_cron_etapa_w
	where	nr_sequencia		= nr_seq_ordem_ativ_w;

	CALL Atualizar_Horas_Etapa_Cron(nr_seq_proj_cron_etapa_w);
	CALL Atualizar_Total_Horas_Cron(nr_seq_cronograma_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
