CREATE OR REPLACE PROCEDURE baca_rateio_ctb_movto () AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_lote_contabil_w		bigint;

c01 CURSOR FOR
SELECT	b.nr_lote_contabil,
	b.nr_sequencia
from	ctb_movto_centro_custo a,
	ctb_movimento b
where	b.nr_sequencia	= a.nr_seq_movimento
and	coalesce(b.vl_movimento,0) <> 0
order by 1,2;


BEGIN

open C01;
loop
fetch C01 into
	nr_lote_contabil_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL ctb_atualizar_rateio_movto(nr_sequencia_w,'TasyRateio');
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
