CREATE OR REPLACE PROCEDURE baca_recalc_protocolo_repasse (nr_seq_protocolO_p bigint) AS $body$
DECLARE

 
nr_interno_conta_w		bigint;

c01 CURSOR FOR 
SELECT	b.nr_interno_conta 
from	conta_paciente b, 
	atendimento_paciente a 
where	a.nr_atendimento	= b.nr_atendimento 
and	b.ie_status_acerto	= 2 
and	coalesce(b.ie_cancelamento::text, '') = '' 
and	b.nr_seq_protocolo	= nr_seq_protocolO_p 
and   Obter_se_repasse_lib_conta(b.nr_interno_conta) = 'N';


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	CALL RECALCULAR_CONTA_REPASSE(nr_interno_conta_w,null,null,'TASY_BACA');
 
	commit;
 
end loop;
close c01;
 
 
end;
