SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_regra_conta_pat_bem () AS $body$
DECLARE



dt_valor_w		timestamp;
nr_sequencia_w		bigint;
qt_commit_w		bigint	:= 0;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.cd_conta_contabil,
	a.cd_estabelecimento
from	pat_bem a,
	pat_conta_contabil b
where	a.cd_conta_contabil	= b.cd_conta_contabil
and	a.cd_estabelecimento	= b.cd_estabelecimento
and	coalesce(a.nr_seq_regra_conta::text, '') = '';

vet01	C01%RowType;


BEGIN

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(dt_valor)
	into STRICT	dt_valor_w
	from	pat_valor_bem
	where	nr_seq_bem	= vet01.nr_sequencia;

	dt_valor_w	:= trunc(coalesce(dt_valor_w, clock_timestamp()),'mm');

	select	coalesce(min(b.nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	pat_conta_contabil b
	where	b.cd_estabelecimento	= vet01.cd_estabelecimento
	and	b.cd_conta_contabil	= vet01.cd_conta_contabil
	and	b.dt_vigencia		<= dt_valor_w
	and	b.ie_situacao		= 'A';

	if (nr_sequencia_w = 0) then

		select	coalesce(min(b.nr_sequencia),0)
		into STRICT	nr_sequencia_w
		from	pat_conta_contabil b
		where	b.cd_estabelecimento	= vet01.cd_estabelecimento
		and	b.cd_conta_contabil	= vet01.cd_conta_contabil
		and	b.dt_vigencia		<= dt_valor_w;

	end if;


	if (nr_sequencia_w <> 0) then
		begin
		update	pat_bem
		set	nr_seq_regra_conta	= nr_sequencia_w
		where	nr_sequencia		= vet01.nr_sequencia;

		update	pat_tipo_bem
		set	nr_seq_regra_conta	= nr_sequencia_w
		where	cd_conta_contabil	= vet01.cd_conta_contabil;


		qt_commit_w	:= qt_commit_w + 1;
		end;
	end if;

	if (qt_commit_w >= 1000) then
		commit;
		qt_commit_w	:= 0;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
