SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_resposta_com_faq () AS $body$
DECLARE


ds_erro_w				varchar(4000)	:= '';
qt_registro_w			bigint;
nm_tabela_bkp_w			varchar(30);

BEGIN

select count(*)
into STRICT	qt_registro_w
from	user_tab_columns
where	table_name	= 'COM_FAQ'
and	column_name	= 'DS_RESPOSTA'
and	data_type	= 'VARCHAR2';

if (qt_registro_w > 0) then
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	COM_FAQ;

	if (qt_registro_w = 0) then
		CALL Exec_Sql_Dinamico('TASY', ' alter table COM_FAQ modify DS_RESPOSTA LONG');
	else
		nm_tabela_bkp_w	:= 'BKP_COM_FAQ';

		select	count(*)
		into STRICT	qt_registro_w
		from	user_tables
		where	table_name	= 'BKP_COM_FAQ';

		if (qt_registro_w > 0) then
			nm_tabela_bkp_w	:= 'BKP_COM_FAQ2';
		end if;

		CALL Exec_Sql_Dinamico('TASY', ' create table ' || nm_tabela_bkp_w || ' as select * from COM_FAQ' );

		select	count(*)
		into STRICT	qt_registro_w
		from	user_tables
		where	table_name	= nm_tabela_bkp_w;

		if (qt_registro_w > 0) then
			begin
			/*Criar campo DS_HISTORICO_TEMP*/

			CALL Exec_Sql_Dinamico('TASY', ' alter table COM_FAQ add DS_RESPOSTA_TEMP VARCHAR2(4000)' );

			CALL wheb_usuario_pck.set_ie_executar_trigger('N');
			/*Copiar conteudo do DS_HISTORICO para DS_HISTORICO_TEMP*/

			begin
			CALL Exec_Sql_Dinamico('TASY', ' update COM_FAQ set DS_RESPOSTA_TEMP = DS_RESPOSTA');
			commit;

			CALL wheb_usuario_pck.set_ie_executar_trigger('S');
			exception when others then
				ds_erro_w	:= substr('ERRO' || sqlerrm(SQLSTATE),1,4000);
				CALL wheb_usuario_pck.set_ie_executar_trigger('S');
			end;

			if (coalesce(ds_erro_w,'X') = 'X') then
				begin
				/*Apagar campo DS_RESPOSTA*/

				CALL Exec_Sql_Dinamico('TASY', ' update COM_FAQ set DS_RESPOSTA = null');

				/*Renomear o novo campo para DS_RESPOSTA*/

				CALL Exec_Sql_Dinamico('TASY', ' alter table COM_FAQ modify DS_RESPOSTA LONG');

				CALL Exec_Sql_Dinamico('TASY', ' update COM_FAQ set DS_RESPOSTA = DS_RESPOSTA_TEMP');
				end;
			end if;
			end;
		end if;
	end if;
	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
