SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_resultado_centro_pk () AS $body$
DECLARE


cd_estabelecimento_w		smallint;
cd_tabela_custo_w			bigint;
cd_centro_controle_w		bigint;
ie_classif_conta_w			integer;
nr_sequencia_w			numeric(20);
qt_registro_w			bigint := 0;
qt_reg_nulo_w			bigint := 0;
c01 CURSOR FOR
SELECT	a.cd_estabelecimento,
	a.cd_tabela_custo,
	a.cd_centro_controle,
	a.ie_classif_conta,
	a.nr_sequencia
from	resultado_centro_controle a
where	coalesce(a.nr_seq_interno::text, '') = ''
order by 1,2,3,4,5;


BEGIN

open C01;
loop
fetch C01 into
	cd_estabelecimento_w,
	cd_tabela_custo_w,
	cd_centro_controle_w,
	ie_classif_conta_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_registro_w		:= qt_registro_w + 1;

	update	resultado_centro_controle
	set	nr_seq_interno	= nextval('resultado_centro_controle_seq')
	where	cd_estabelecimento	= cd_estabelecimento_w
	and	cd_tabela_custo	= cd_tabela_custo_w
	and	cd_centro_controle	= cd_centro_controle_w
	and	ie_classif_conta	= ie_classif_conta_w
	and	nr_sequencia	= nr_sequencia_w;

	if (qt_registro_w >= 5000) then
		qt_registro_w	:= 0;
		commit;
	end if;
	end;
end loop;
close C01;

select	count(*)
into STRICT	qt_reg_nulo_w
from	resultado_centro_controle
where	coalesce(nr_seq_interno::text, '') = '';

select	count(*)
into STRICT	qt_registro_w
from	user_cons_columns
where	constraint_name 	= 'RESCECO_PK'
and	column_name	= 'NR_SEQ_INTERNO';

if (qt_reg_nulo_w = 0) and (qt_registro_w = 0) then
	/* Elimina Primary Key antiga*/

	CALL exec_sql_dinamico('RESCECO_PK','alter table RESULTADO_CENTRO_CONTROLE drop constraint RESCECO_PK');
	CALL exec_sql_dinamico('RESCECO_PK','drop index RESCECO_PK'); /* Força eliminar o indice nos casos que o banco cria obj diferentes com mesmo nome*/
	CALL exec_sql_dinamico('RESCECO_PK','alter table RESULTADO_CENTRO_CONTROLE modify NR_SEQ_INTERNO not null');
	/* Cria a nova chave primária */

	CALL exec_sql_dinamico('RESCECO_PK','Alter Table RESULTADO_CENTRO_CONTROLE add (Constraint RESCECO_PK Primary Key (NR_SEQ_INTERNO) Using Index Tablespace TASY_INDEX)');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
