SET client_encoding TO 'UTF8';



CREATE TYPE campos AS (nr_interno_conta bigint);


CREATE OR REPLACE PROCEDURE baca_resumo_conta ( dt_parametro_P timestamp) AS $body$
DECLARE

type Vetor is table of campos index by integer;

NR_INTERNO_CONTA_W         	bigint;
i					integer;
k					integer;
Vetor_Proc_w				Vetor;

 
C01 CURSOR FOR 
SELECT a.nr_interno_conta 
from 	conta_paciente a 
where a.dt_mesano_referencia >= trunc(dt_parametro_p,'dd') 
 and a.ie_status_acerto 		= 2 
 and not exists (SELECT 1 from conta_paciente_resumo b 
	 where a.nr_interno_conta = b.nr_interno_conta);


BEGIN 
 
i	:= 1;
OPEN C01;
LOOP 
FETCH C01 into	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	Vetor_Proc_w[i].nr_interno_conta := nr_interno_conta_w;
	i := i + 1;
	end;
END LOOP;
close c01;
 
i := Vetor_Proc_w.count;
commit;
 
for k in 1.. i loop 
	begin 
	nr_interno_conta_w	:= Vetor_Proc_w[k].nr_interno_conta;
	/*if	(trunc(k /100,0) = (k / 100)) then 
		insert into logxxxx_tasy values(sysdate, 'Tasy', 999, k || '/' || i); 
	end if;*/
 
	CALL atualizar_resumo_conta(nr_interno_conta_w, 2);
	commit;
	end;
end loop;
/*insert into logxxx_tasy values(sysdate, 'Tasy', 999, k || '/' || i);*/
 
commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
