SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_rhc_exames_relevantes () AS $body$
DECLARE



ie_exame_relevante_w	bigint;
nr_sequencia_w		bigint;

c01 CURSOR FOR
	SELECT	ie_exame_relevante,
		nr_sequencia
	from	can_ficha_admissao
	where	(ie_exame_relevante IS NOT NULL AND ie_exame_relevante::text <> '');


BEGIN

/*Baca com objetivo de separa o campo ie_exame_relevante e v√°rios checkbox */

open	c01;
loop
fetch	c01 into
	ie_exame_relevante_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (ie_exame_relevante_w = 1) then
		update	can_ficha_admissao
		set	ie_erd_exame_clinico	= 'S'
		where	nr_sequencia	= nr_sequencia_w;
	elsif (ie_exame_relevante_w = 2) then
		update	can_ficha_admissao
		set	ie_erd_exame_imagem	= 'S'
		where	nr_sequencia	= nr_sequencia_w;
	elsif (ie_exame_relevante_w = 3) then
		update	can_ficha_admissao
		set	ie_erd_endoscopia	= 'S'
		where	nr_sequencia	= nr_sequencia_w;
	elsif (ie_exame_relevante_w = 4) then
		update	can_ficha_admissao
		set	ie_erd_anatomia_pat	= 'S'
		where	nr_sequencia	= nr_sequencia_w;
	elsif (ie_exame_relevante_w = 8) then
		update	can_ficha_admissao
		set	ie_erd_nao_se_aplica	= 'S'
		where	nr_sequencia	= nr_sequencia_w;
	elsif (ie_exame_relevante_w = 9) then
		update	can_ficha_admissao
		set	ie_erd_sem_informacao	= 'S'
		where	nr_sequencia	= nr_sequencia_w;
	end if;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
