CREATE OR REPLACE PROCEDURE baca_sus_atualiza_campos_prot ( nr_seq_protocolo_p bigint, cd_tipo_logradouro_p text) AS $body$
DECLARE

 
 
nr_atendimento_w	bigint;
nr_interno_conta_w	bigint;
cd_pessoa_fisica_w	varchar(10);
nr_seq_cor_pele_w	bigint;
nr_seq_cor_pele_ww	bigint;
cd_tipo_logradouro_w	varchar(3);
cd_tipo_logradouro_ww	varchar(3);
qt_log_valido_w		integer	:= 0;

 
C01 CURSOR FOR 
	SELECT	b.nr_atendimento, 
		a.nr_interno_conta, 
		c.cd_pessoa_fisica, 
		coalesce(c.nr_seq_cor_pele,0), 
		obter_compl_pf(c.cd_pessoa_fisica, 1, 'TLS') 
	from	pessoa_fisica		c, 
		atendimento_paciente	b, 
		conta_paciente		a 
	where	a.nr_seq_protocolo	= nr_seq_protocolo_p 
	and	a.nr_atendimento	= b.nr_atendimento 
	and	b.cd_pessoa_fisica	= c.cd_pessoa_fisica 
	and	((coalesce(c.nr_seq_cor_pele::text, '') = '') or (coalesce(obter_compl_pf(c.cd_pessoa_fisica, 1, 'TLS')::text, '') = ''));

 

BEGIN 
 
select	coalesce(min(nr_sequencia),0) 
into STRICT	nr_seq_cor_pele_ww 
from	cor_pele 
where	(cd_raca_cor_sus IS NOT NULL AND cd_raca_cor_sus::text <> '');
 
if (coalesce(cd_tipo_logradouro_p::text, '') = '') then 
	select	coalesce(min(cd_tipo_logradouro),0) 
	into STRICT	cd_tipo_logradouro_ww 
	from	sus_tipo_logradouro;
else 
	select	count(*) 
	into STRICT	qt_log_valido_w 
	from	sus_tipo_logradouro 
	where	cd_tipo_logradouro	= cd_tipo_logradouro_p;
	if (qt_log_valido_w	> 0) then 
		cd_tipo_logradouro_ww	:= cd_tipo_logradouro_p;
	end if;
end if;
 
OPEN C01;
LOOP 
FETCH C01 into 
	nr_atendimento_w, 
	nr_interno_conta_w, 
	cd_pessoa_fisica_w, 
	nr_seq_cor_pele_w, 
	cd_tipo_logradouro_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	if (nr_seq_cor_pele_w	= 0) and (nr_seq_cor_pele_ww 	> 0) then 
		update	pessoa_fisica 
		set	nr_seq_cor_pele		= nr_seq_cor_pele_ww 
		where	cd_pessoa_fisica	= cd_pessoa_fisica_w;
		 
	end if;
 
	if (coalesce(cd_tipo_logradouro_w::text, '') = '') and (cd_tipo_logradouro_ww > 0) then 
		update	compl_pessoa_fisica 
		set	cd_tipo_logradouro	= cd_tipo_logradouro_ww 
		where	cd_pessoa_fisica	= cd_pessoa_fisica_w 
		and	ie_tipo_complemento	= 1;
 
	end if;
 
	commit;
 
	end;
END LOOP;
CLOSE C01;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
