CREATE OR REPLACE PROCEDURE baca_sus_atualiza_carater_prot ( nr_seq_protocolo_p bigint) AS $body$
DECLARE

 
 
nr_atendimento_w	bigint;
nr_interno_conta_w	bigint;
ie_carater_inter_sus_w	varchar(2);

 
C01 CURSOR FOR 
	SELECT	b.nr_atendimento, 
		a.nr_interno_conta, 
		b.ie_carater_inter_sus 
	from	atendimento_paciente	b, 
		conta_paciente		a 
	where	a.nr_seq_protocolo	= nr_seq_protocolo_p 
	and	a.nr_atendimento	= b.nr_atendimento;

 

BEGIN 
 
OPEN C01;
LOOP 
FETCH C01 into 
	nr_atendimento_w, 
	nr_interno_conta_w, 
	ie_carater_inter_sus_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	if (ie_carater_inter_sus_w	= '1') then 
		update	atendimento_paciente 
		set	ie_carater_inter_sus	= '01' 
		where	nr_atendimento		= nr_atendimento_w;
 
	elsif (ie_carater_inter_sus_w	= '5') then 
		update	atendimento_paciente 
		set	ie_carater_inter_sus	= '02' 
		where	nr_atendimento		= nr_atendimento_w;
 
	end if;
 
	commit;
	end;
END LOOP;
CLOSE C01;
 
END;
