CREATE OR REPLACE PROCEDURE baca_sus_atualiza_medico_prot ( nr_seq_protocolo_p bigint) AS $body$
DECLARE

 
 
nr_interno_conta_w	bigint;
nr_atendimento_w	bigint;
cd_medico_executor_w	varchar(10);

 
C01 CURSOR FOR 
	SELECT	a.nr_interno_conta, 
		a.nr_atendimento, 
		b.cd_medico_executor 
	from	procedimento_paciente	b, 
		conta_paciente		a 
	where	a.nr_seq_protocolo	= nr_seq_protocolo_p 
	and	a.nr_interno_conta	= b.nr_interno_conta 
	and	coalesce(b.cd_motivo_exc_conta::text, '') = '' 
	and	b.cd_procedimento	= 0301010072 
	and	b.ie_origem_proced	= 7 
	and	(b.cd_medico_executor IS NOT NULL AND b.cd_medico_executor::text <> '');

 

BEGIN 
 
OPEN C01;
LOOP 
FETCH C01 into 
	nr_interno_conta_w, 
	nr_atendimento_w, 
	cd_medico_executor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	update	procedimento_paciente 
	set	cd_medico_executor	= cd_medico_executor_w 
	where	coalesce(cd_medico_executor::text, '') = '' 
	and	cd_procedimento in (SELECT cd_procedimento from sus_estrutura_procedimento_v where cd_grupo = 3 and ie_origem_proced = 7) 
	and	ie_origem_proced = 7 
	and	coalesce(cd_motivo_exc_conta::text, '') = '' 
	and	nr_interno_conta	= nr_interno_conta_w 
	and	nr_atendimento		= nr_atendimento_w;
 
	commit;
	end;
END LOOP;
CLOSE C01;
 
END;
