SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_sus_atualiza_via () AS $body$
DECLARE

 
 
ie_via_tratamento_w	varchar(5);
nr_seq_interno_w	bigint;
qt_reg_w		bigint;
ds_comando_w		varchar(2000);

c01 CURSOR FOR 
	SELECT	ie_via_tratamento, 
		nr_seq_interno 
	from	sus_laudo_paciente 
	where	(ie_via_tratamento IS NOT NULL AND ie_via_tratamento::text <> '') 
	and	ie_classificacao	= 11;


BEGIN 
 
select	count(*) 
into STRICT	qt_reg_w 
from	log_tasy 
where	cd_log	= 1230;
 
if (qt_reg_w	= 0) then 
	lock table sus_laudo_paciente in exclusive mode;
	ds_comando_w	:= 'Alter trigger SUS_LAUDO_PACIENTE_UPDATE disable';
	CALL Exec_Sql_Dinamico('Disable trigger', ds_comando_w);
	 
	open	c01;
	loop 
	fetch	c01 into 
		ie_via_tratamento_w, 
		nr_seq_interno_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		if (ie_via_tratamento_w	= 'IV') then 
			update	sus_laudo_paciente 
			set	ie_via_iv	= 'S' 
			where	nr_seq_interno	= nr_Seq_interno_w;
 
		elsif (ie_via_tratamento_w	= 'SC') then 
			update	sus_laudo_paciente 
			set	ie_via_sc	= 'S' 
			where	nr_seq_interno	= nr_Seq_interno_w;
	 
		elsif (ie_via_tratamento_w	= 'IM') then 
			update	sus_laudo_paciente 
			set	ie_via_im	= 'S' 
			where	nr_seq_interno	= nr_Seq_interno_w;
 
		elsif (ie_via_tratamento_w	= 'VO') then 
			update	sus_laudo_paciente 
			set	ie_via_vo	= 'S' 
			where	nr_seq_interno	= nr_Seq_interno_w;
	 
		elsif (ie_via_tratamento_w	= 'IT') then 
			update	sus_laudo_paciente 
			set	ie_via_it	= 'S' 
			where	nr_seq_interno	= nr_Seq_interno_w;
	 
		elsif (ie_via_tratamento_w	= 'IS') then 
			update	sus_laudo_paciente 
			set	ie_via_ives	= 'S' 
			where	nr_seq_interno	= nr_Seq_interno_w;
		end if;
		end;
	end loop;
	close c01;
 
	ds_comando_w	:= 'Alter trigger SUS_LAUDO_PACIENTE_UPDATE enable';
	CALL Exec_Sql_Dinamico('Disable trigger', ds_comando_w);
 
	commit;
end if;
 
End;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
