i				bigint		:= 0;

C01 CURSOR FOR 
SELECT nr_atendimento, 
	ie_tipo_laudo_sus, 
	nr_laudo_sus 
from 	sus_laudo_paciente 
where	coalesce(nr_seq_interno::text, '') = '';


