CREATE OR REPLACE PROCEDURE baca_tabela_visao_atributo () AS $body$
DECLARE



vl_padrao_w	varchar(50);
nr_sequencia_w	bigint;
nm_atributo_w	varchar(50);

C01 CURSOR FOR
	SELECT	a.vl_default,
		c.nr_sequencia,
		c.nm_atributo
	from	tabela_atributo a,
		tabela_visao	b,
		tabela_visao_atributo c
	where	a.nm_tabela = b.nm_tabela
	and	b.nr_sequencia = c.nr_sequencia
	and	a.nm_atributo = c.nm_atributo
	and	(a.vl_default IS NOT NULL AND a.vl_default::text <> '')
	and	coalesce(c.vl_padrao::text, '') = '';

BEGIN


open C01;
loop
fetch C01 into
	vl_padrao_w,
	nr_sequencia_w,
	nm_atributo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

		update tabela_visao_atributo
		set	vl_padrao = vl_padrao_w
		where	nr_sequencia = nr_sequencia_w
		and	nm_atributo = nm_atributo_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
