CREATE OR REPLACE PROCEDURE baca_tasymed_profissional ( cd_profissional_p text, nm_usuario_p text, nr_seq_tipo_pepa_p bigint default null) AS $body$
DECLARE

				

cd_pessoa_fisica_w		varchar(10);
				
C01 CURSOR FOR
	SELECT	distinct(a.cd_pessoa_fisica)
	from	med_cliente a
	where	a.cd_medico    =	cd_profissional_p
	and		coalesce(a.ie_exportado,'N') = 'N'	
	order by a.cd_pessoa_fisica;
	

BEGIN


open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
begin

	if (coalesce(nr_seq_tipo_pepa_p::text, '') = '') then
		-- Evolucoes
		
		CALL baca_importa_evol_tasymed(cd_pessoa_fisica_w,cd_profissional_p,nm_usuario_p, null);
		
		-- Atestados
		
		CALL baca_importa_atestado_tasymed(cd_pessoa_fisica_w,cd_profissional_p,nm_usuario_p, null);
		
		-- Solicitacoes de exames
		
		CALL baca_importa_exames_tasymed(cd_pessoa_fisica_w,cd_profissional_p,nm_usuario_p, null);
		
		-- Parecer Medico
	
		CALL baca_importa_parecer_tasymed(cd_pessoa_fisica_w,cd_profissional_p,nm_usuario_p, null);
		
		-- Demais itens
		
		CALL Baca_importa_reg_tasymed(cd_pessoa_fisica_w,cd_profissional_p,nm_usuario_p, null);
		
		
		update 	med_cliente
		set		ie_exportado = 'S',
				dt_exportacao = clock_timestamp(),
				nm_usuario_exportacao = nm_usuario_p
		where	cd_pessoa_fisica = cd_pessoa_fisica_w
		and		cd_medico = cd_profissional_p;
		
		commit;
	
	else
	
		CALL baca_tasymed_consulta(cd_pessoa_fisica_w,cd_profissional_p,nm_usuario_p,nr_seq_tipo_pepa_p);
	
	end if;
	
	
end;
end loop;
close C01;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
