SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_tipo_cheque_cr () AS $body$
DECLARE


qt_registro_w	bigint;

c01 CURSOR FOR
SELECT	a.nr_seq_cheque,
		substr(obter_se_cheque_cr_avista(a.nr_seq_cheque),1,1) ie_avista
from	cheque_cr a
where	coalesce(a.ie_forma_pagto::text, '') = '';

vet01	c01%RowType;

type cheques is table of c01%Rowtype index by integer;

cheques_w	cheques;
i		integer	:= 0;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	cheque_cr
where	coalesce(ie_forma_pagto::text, '') = '';

if (qt_registro_w > 0) then

	open C01;
	loop
	fetch C01 into
		vet01;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		i	:= i + 1;
		cheques_w[i].nr_seq_cheque	:= vet01.nr_seq_cheque;
		cheques_w[i].ie_avista		:= vet01.ie_avista;
		end;
	end loop;
	close C01;

	for i in 1..cheques_w.count loop
		begin
		update	cheque_cr
		set		ie_forma_pagto	= CASE WHEN cheques_w[i].ie_avista='S' THEN 'AV'  ELSE 'PD' END
		where	nr_seq_cheque	= cheques_w[i].nr_seq_cheque;
		end;
	end loop;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
