CREATE OR REPLACE PROCEDURE baca_tipo_gasto () AS $body$
DECLARE


cd_estabelecimento_w		bigint;
cd_tabela_custo_w		bigint;
cd_centro_controle_w		integer;
cd_natureza_gasto_w		numeric(20);
ie_tipo_gasto_w			varchar(2);
qt_registro_w			bigint;

c02 CURSOR FOR
SELECT	cd_estabelecimento,
	cd_tabela_custo,
	cd_centro_controle,
	cd_natureza_gasto
from	orcamento_custo
where	coalesce(ie_tipo_gasto::text, '') = ''
order by 1,2,3,4;


BEGIN

qt_registro_w	:= 0;
open c02;
loop
fetch c02 into
	cd_estabelecimento_w,
	cd_tabela_custo_w,
	cd_centro_controle_w,
	cd_natureza_gasto_w;
EXIT WHEN NOT FOUND; /* apply on c02 */

	qt_registro_w	:= qt_registro_w + 1;
	ie_tipo_gasto_w	:= cus_obter_tipo_gasto_centro(cd_estabelecimento_w, cd_centro_controle_w, null, cd_natureza_gasto_w, null);

	update	orcamento_custo
	set	ie_tipo_gasto		= ie_tipo_gasto_w
	where	cd_estabelecimento	= cd_estabelecimento_w
	and	cd_tabela_custo	= cd_tabela_custo_w
	and	cd_centro_controle	= cd_centro_controle_W
	and	cd_natureza_gasto	= cd_natureza_gasto_w;

	if (qt_registro_w >= 1000) then
		qt_registro_w	:= 0;
		commit;
	end if;
end loop;
close c02;
commit;
end;
$body$
