CREATE OR REPLACE PROCEDURE baca_titulo_pagar () AS $body$
DECLARE

nr_sequencia_w	    		bigint;
C010 CURSOR FOR
	SELECT distinct nr_sequencia 
	from 	nota_fiscal_venc 
	where dt_vencimento > clock_timestamp() - interval '1 days';


BEGIN 
OPEN C010;
LOOP 
	FETCH C010 into 
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c010 */
      begin 
		CALL gerar_titulo_pagar_nf(nr_sequencia_w, 'Tasy');
		end;
END LOOP;
CLOSE C010;
COMMIT;
END;
