CREATE OR REPLACE PROCEDURE baca_titulo_receber_hist () AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

select 	count(*)
into STRICT	qt_registros_w
from 	TITULO_RECEBER_HIST a
where 	not exists (SELECT	1
	from	TITULO_RECEBER b
	where 	a.NR_TITULO	= b.NR_TITULO)
and 	(a.NR_TITULO IS NOT NULL AND a.NR_TITULO::text <> '');

if (qt_registros_w > 0) then

	CALL Exec_Sql_Dinamico('TITRECH_TITRECE_FK',	' delete from TITULO_RECEBER_HIST a '||
						' where not exists (select 1 from TITULO_RECEBER b where a.NR_TITULO = b.NR_TITULO) '||
						' and a.NR_TITULO is not null ');

	CALL Exec_Sql_Dinamico('TITRECH_TITRECE_FK', ' Alter Table TITULO_RECEBER_HIST drop Constraint TITRECH_TITRECE_FK ');

	CALL Exec_Sql_Dinamico('TITRECH_TITRECE_FK', ' Alter Table TITULO_RECEBER_HIST add (Constraint TITRECH_TITRECE_FK Foreign Key (NR_TITULO) ' ||
						' References TITULO_RECEBER(NR_TITULO) on delete cascade) ');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
