CREATE OR REPLACE PROCEDURE baca_titulo_rec_liq_cc (cd_estabelecimento_p bigint, nr_seq_retorno_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_lote_contabil_w		bigint;
cont_w				bigint;
nr_titulo_w			bigint;
nr_seq_baixa_w			bigint;

c01 CURSOR FOR 
SELECT	b.nr_titulo, 
	b.nr_sequencia 
from	titulo_receber_liq b, 
	convenio_retorno_item a 
where	a.nr_seq_retorno	= nr_seq_retorno_p 
and	a.nr_sequencia		= b.nr_seq_ret_item 
and (coalesce(b.vl_glosa,0)		<> 0 or 
	coalesce(b.vl_rec_maior,0) 		<> 0);


BEGIN 
 
select	max(b.nr_lote_contabil) 
into STRICT	nr_lote_contabil_w 
from	titulo_receber_liq b, 
	convenio_retorno_item a 
where	a.nr_sequencia		= b.nr_seq_ret_item 
and	a.nr_seq_retorno	= nr_seq_retorno_p;
 
if (coalesce(nr_lote_contabil_w,0) > 0) then 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(280835, 'NR_LOTE_CONTABIL_P=' || nr_lote_contabil_w);
end if;
 
select	count(*) 
into STRICT	cont_w 
from	user_tables 
where	table_name	= 'BKP_TITULO_REC_LIQ_CC';
 
if (cont_w = 0) then 
	CALL Exec_Sql_Dinamico('TASY', 'create table BKP_TITULO_REC_LIQ_CC as select * from titulo_rec_liq_cc');
end if;
 
select	count(*) 
into STRICT	cont_w 
from	user_tables 
where	table_name	= 'BKP_TITULO_REC_LIQ_CC';
 
if (cont_w = 0) then 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(280843);
end if;
 
 
open c01;
loop 
fetch c01 into 
	nr_titulo_w, 
	nr_seq_baixa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	delete 	from titulo_rec_liq_cc a 
	where	a.nr_titulo		= nr_titulo_w 
	and	a.nr_seq_baixa		= nr_seq_baixa_w;
 
	CALL gerar_titulo_rec_liq_cc(cd_estabelecimento_p, null, nm_usuario_p, nr_titulo_w, nr_seq_baixa_w);
 
end loop;
close c01;
 
commit;
 
end;
$body$
