SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_trans_fin_contab_cb () AS $body$
DECLARE


cd_historico_banco_w	bigint;
ie_contab_cb_w		varchar(1);
nr_sequencia_w		bigint;
qt_commit_w		bigint	:= 0;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.cd_historico_banco
from	transacao_financeira a
where	coalesce(a.ie_contab_cb,'X') = 'X';


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	cd_historico_banco_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_commit_w	:= qt_commit_w + 1;

	ie_contab_cb_w	:= 'N';

	if (cd_historico_banco_w IS NOT NULL AND cd_historico_banco_w::text <> '') then
		ie_contab_cb_w	:= 'S';
	end if;

	update	transacao_financeira
	set	ie_contab_cb	= ie_contab_cb_w
	where	nr_sequencia	= nr_sequencia_w;

	if (qt_commit_w >= 100) then
		commit;
		qt_commit_w	:= 0;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
