SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_trans_fin_rep_maior () AS $body$
DECLARE


cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	procedimento_repasse
where	coalesce(NR_SEQ_TRANS_FIN_REP_MAIOR::text, '') = ''
and	coalesce(nr_lote_contabil,0)	= 0;

while(cont_w	> 0) loop

	update 	procedimento_repasse a
	set	a.NR_SEQ_TRANS_FIN_REP_MAIOR = (SELECT	x.NR_SEQ_TRANS_FIN_REP_MAIOR
						from	regra_repasse_terceiro x
						where	x.cd_regra	= a.cd_regra)
	where	coalesce(a.NR_SEQ_TRANS_FIN_REP_MAIOR::text, '') = ''
	and	coalesce(a.nr_lote_contabil,0)	= 0  LIMIT 5000;

	cont_w	:= cont_w - 5000;

	commit;
end loop;

select	count(*)
into STRICT	cont_w
from	material_repasse
where	coalesce(NR_SEQ_TRANS_FIN_REP_MAIOR::text, '') = ''
and	coalesce(nr_lote_contabil,0)	= 0;

while(cont_w	> 0) loop
	update 	material_repasse a
	set	a.NR_SEQ_TRANS_FIN_REP_MAIOR = (SELECT	x.NR_SEQ_TRANS_FIN_REP_MAIOR
						from	regra_repasse_terceiro x
						where	x.cd_regra	= a.cd_regra)
	where	coalesce(a.NR_SEQ_TRANS_FIN_REP_MAIOR::text, '') = ''
	and	coalesce(a.nr_lote_contabil,0)	= 0  LIMIT 5000;

	cont_w	:= cont_w - 5000;

	commit;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
