SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_trib_tit_pagar () AS $body$
DECLARE


ie_periodicidade_w	varchar(1);
cd_variacao_w		varchar(2);
nr_sequencia_w		bigint;
cd_tributo_w		bigint;
ds_tributo_w		varchar(40);

c01 CURSOR FOR
SELECT	b.nr_sequencia
FROM	titulo_pagar_imposto b,
	titulo_pagar a
WHERE	a.nr_titulo	= b.nr_titulo
AND	a.dt_emissao	> TO_DATE('01/01/2007','dd/mm/yyyy')
and (coalesce(b.ie_periodicidade::text, '') = '' or coalesce(b.cd_variacao::text, '') = '')
and	exists (select	1
		from	tributo_dctf x
		where	x.cd_tributo	= b.cd_tributo);


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select	a.cd_tributo,
		b.ds_tributo
	into STRICT	cd_tributo_w,
		ds_tributo_w
	from	tributo b,
		titulo_pagar_imposto a
	where	a.cd_tributo	= b.cd_tributo
	and	a.nr_sequencia	= nr_sequencia_w;

	begin
	SELECT 	a.ie_periodicidade,
		a.cd_variacao
	into STRICT	ie_periodicidade_w,
		cd_variacao_w
	FROM	tributo_dctf a
	WHERE	a.cd_tributo = cd_tributo_w
	and	a.dt_inicio_vigencia	= (SELECT	max(x.dt_inicio_vigencia)
					   from		tributo_dctf x
					   where	x.cd_tributo	= a.cd_tributo
					   and		trunc(x.dt_inicio_vigencia,'dd') <= trunc(clock_timestamp(),'dd'));
	exception
		when no_data_found then
			RAISE EXCEPTION '%', 'Verifique se hÃ¡ cadastro de DCTF para o tributo: ' || ds_tributo_w USING ERRCODE = '45011';
	end;

	update	titulo_pagar_imposto
	set	ie_periodicidade	= ie_periodicidade_w,
		cd_variacao		= cd_variacao_w
	where	nr_sequencia		= nr_sequencia_w;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
