SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_valor_evento_movimento () AS $body$
BEGIN
update	pls_evento_movimento	z
set	z.vl_movimento		= abs(z.vl_movimento)
where	coalesce(z.vl_movimento,0) <> 0;

update	pls_evento_movimento	z
set	z.vl_movimento		= (z.vl_movimento * (-1))
where	coalesce(z.vl_movimento,0) <> 0
and exists (	SELECT	1
		from	pls_evento_movimento	b,
			pls_evento		a
		where	a.nr_sequencia		= b.nr_seq_evento
		and	b.nr_sequencia		= z.nr_sequencia
		and	a.ie_natureza		= 'D');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
