SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_via_forma_farm () AS $body$
DECLARE


cd_material_w		integer;
nr_seq_forma_farm_w	bigint;
nm_usuario_w		varchar(15);
qt_cont_w		numeric(20) := 0;

c01 CURSOR FOR
	SELECT	cd_material,
		nr_seq_forma_farm,
		nm_usuario
	from	material
	where	(nr_seq_forma_farm IS NOT NULL AND nr_seq_forma_farm::text <> '');


BEGIN

open c01;
	loop
	fetch c01 into
		cd_material_w,
		nr_seq_forma_farm_w,
		nm_usuario_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		CALL Atualiza_Via_Forma_Farm(cd_material_w,nr_seq_forma_farm_w,nm_usuario_w);
		qt_cont_w	:= qt_cont_w + 1;
		if (qt_cont_w = 50000) then
			begin
			qt_cont_w	:= 0;
			commit;
			end;
		end if;
		end;
	end loop;
close c01;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
