CREATE OR REPLACE PROCEDURE bac_sequence_contr_renovacao () AS $body$
DECLARE


qt_registros_w	bigint;
nr_seq_proxima	bigint;

BEGIN

select 	count(*)
into STRICT	qt_registros_w
from 	user_sequences
where 	sequence_name = 'CONTRATO_RENOVACAO_SEQ';

if (qt_registros_w = 0) then

	select 	coalesce(max(nr_sequencia) ,0)
	into STRICT	nr_seq_proxima
	from	CONTRATO_RENOVACAO;

	CALL Exec_Sql_Dinamico('Bruno', 	' create sequence CONTRATO_RENOVACAO_SEQ ' ||
					' increment by 1 ' ||
					' start with  ' || (nr_seq_proxima + 1) ||
					' maxValue 9999999999 ' ||
					' cycle ' ||
					' nocache');

else
	CALL tasy_posicionar_sequence('CONTRATO_RENOVACAO','NR_SEQUENCIA','N');
end if;


commit;

end;
