SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baixar_emprestimo_material ( nr_emprestimo_p bigint, nr_sequencia_p bigint, nm_usuario_p text, qt_material_p bigint default null) AS $body$
DECLARE


cd_material_w		emprestimo_material.cd_material%type;
qt_material_w		emprestimo_material.qt_material%type;
nr_seq_dev_w		emprestimo_material_dev.nr_seq_dev%type;
nr_seq_equipamento_w	rp_equipamento.nr_seq_equipamento%type;
ds_texto_w		varchar(255);


BEGIN

select	cd_material,
	qt_material
into STRICT	cd_material_w,
	qt_material_w
from	emprestimo_material
where	nr_emprestimo	= nr_emprestimo_p
and	nr_sequencia	= nr_sequencia_p;

select	coalesce(max(nr_seq_dev),0)
into STRICT	nr_seq_dev_w
from	emprestimo_material_dev
where	nr_emprestimo	= nr_emprestimo_p
and	nr_sequencia	= nr_sequencia_p;

nr_seq_dev_w		:= nr_seq_dev_w + 1;

insert	into emprestimo_material_dev(
	nr_emprestimo,
	nr_sequencia,
	nr_seq_dev,
	cd_material,
	qt_material,
	dt_atualizacao,
	nm_usuario)
values (nr_emprestimo_p,
	nr_sequencia_p,
	nr_seq_dev_w,
	cd_material_w,
	coalesce(qt_material_p, qt_material_w),
	clock_timestamp(),
	nm_usuario_p);

select 	coalesce(max(nr_seq_equipamento),0)
into STRICT	nr_seq_equipamento_w
from	rp_equipamento
where	nr_emprestimo 		= nr_emprestimo_p
and	nr_seq_emp_material 	= nr_sequencia_p;
	
if (nr_seq_equipamento_w > 0) then

	update	rp_equipamento
	set	dt_fim_utilizacao	= clock_timestamp(),
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_emprestimo 		= nr_emprestimo_p
	and	nr_seq_emp_material 	= nr_sequencia_p
	and	coalesce(dt_fim_utilizacao::text, '') = '';

	ds_texto_w := substr(obter_texto_dic_objeto(279805, 1, 'NR_EMPRESTIMO=' || nr_emprestimo_p),1,255);
	
	-- Registrar Baixa de Emprestimo no historico do equipamento:
	CALL man_log_equip_hist_evento(	nr_seq_equipamento_w,
					ds_texto_w,
					'B',
					nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
