SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baixar_emprestimo_todos_itens ( nr_emprestimo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		integer;
cd_material_w		integer;
qt_material_w		double precision;
nr_seq_dev_w		integer;
qt_material_lote_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia,
	cd_material,
	qt_material
from	emprestimo_material
where	nr_emprestimo	= nr_emprestimo_p
and	qt_material	> 0;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	cd_material_w,
	qt_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select 	count(*)
	into STRICT  	qt_material_lote_w
	from 	emprestimo_material
	where 	obter_situacao_emprestimo_mat(nr_emprestimo_p,nr_sequencia_w) <> 'B'
	and  	obter_se_material_estoque_lote(wheb_usuario_pck.get_cd_estabelecimento, cd_material_w) = 'S'
	and 	nr_emprestimo = nr_emprestimo_p
	and     cd_material = cd_material_w;
		
	if (qt_material_lote_w > 0) then
	
	RAISE EXCEPTION '%', wheb_mensagem_pck.get_texto(1115952) USING ERRCODE = '45011';
	
	end if;

	update	emprestimo_material
	set	qt_material = 0
	where	nr_emprestimo = nr_emprestimo_p
	and	nr_sequencia = nr_sequencia_w;


	select	coalesce(max(nr_seq_dev),0)
	into STRICT	nr_seq_dev_w
	from	emprestimo_material_dev
	where	nr_emprestimo	= nr_emprestimo_p
	and	nr_sequencia	= nr_sequencia_w;

	nr_seq_dev_w		:= nr_seq_dev_w + 1;

	insert	into emprestimo_material_dev(
		nr_emprestimo,
		nr_sequencia,
		nr_seq_dev,
		cd_material,
		qt_material,
		dt_atualizacao,
		nm_usuario)
	values ( nr_emprestimo_p,
		nr_sequencia_w,
		nr_seq_dev_w,
		cd_material_w,
		qt_material_w,
		clock_timestamp(),
		nm_usuario_p);
	end loop;
	close c01;

update	emprestimo
set	ie_situacao = 'B'
where	nr_emprestimo = nr_emprestimo_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
