CREATE OR REPLACE PROCEDURE baixar_estoque_perda_js ( nr_seq_item_cabine_p bigint, nr_seq_cabine_p bigint, nr_seq_ordem_p bigint, cd_material_p bigint, cd_material_prescr_p bigint, qt_estoque_p bigint, cd_unidade_medida_p text, nm_usuario_p text, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint) AS $body$
DECLARE


ie_utiliza_perda_farmacia_w	varchar(1);
ie_medic_perda_w		varchar(1);
ds_erro_w			varchar(255);

BEGIN

ds_erro_w := baixar_estoque_perda(
	nr_seq_item_cabine_p, nr_seq_cabine_p, cd_material_p, qt_estoque_p, cd_unidade_medida_p, nm_usuario_p, cd_setor_atendimento_p, null, null, null, null, ds_erro_w);

if (cd_material_p = cd_material_prescr_p) then
	begin
	/* Quimioterapia - Parâmetro [210] - Permite ao farmacêutico o registro de perda dos medicamentos */

	ie_utiliza_perda_farmacia_w := obter_param_usuario(3130, 210, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_utiliza_perda_farmacia_w);

	if (ie_utiliza_perda_farmacia_w = 'S') then
		begin
		update	can_ordem_item_prescr
		set	dt_solic_perda	 = NULL
		where	nr_seq_ordem	= nr_seq_ordem_p
		and	cd_material	= cd_material_prescr_p;

		commit;

		select	coalesce(obter_se_medic_perda(nr_seq_ordem_p), 'S')
		into STRICT	ie_medic_perda_w
		;

		if (ie_medic_perda_w = 'S') then
			begin
			update	can_ordem_prod
			set	dt_solic_perda	 = NULL
			where	nr_sequencia	= nr_seq_ordem_p;
			end;
		end if;
		end;
	end if;
	end;
end if;

commit;

end;
$body$
