CREATE OR REPLACE PROCEDURE baixar_horarios_proc_esp (nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_procedimento_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE

 
nr_seq_horario_w		bigint;
dt_horario_w			timestamp;
cd_procedimento_w		bigint;
nr_seq_proc_interno_w		bigint;
nr_seq_alteracao_w		bigint;
ie_tipo_item_w			varchar(10);

c01 CURSOR FOR 
SELECT	nr_sequencia, 
	dt_horario, 
	cd_procedimento, 
	nr_seq_proc_interno 
from	prescr_proc_hor 
where	nr_prescricao		= nr_prescricao_p 
and coalesce(nr_seq_proc_origem,nr_seq_procedimento) 	= nr_seq_procedimento_p 
and	coalesce(dt_fim_horario::text, '') = '' 
and	coalesce(dt_suspensao::text, '') = '';

c02 CURSOR FOR 
SELECT	nr_sequencia, 
	dt_horario, 
	cd_procedimento, 
	nr_seq_proc_interno 
from	prescr_proc_hor 
where	nr_prescricao		= nr_prescricao_p 
and coalesce(nr_seq_proc_origem,nr_seq_procedimento) 	= nr_seq_procedimento_p 
and	coalesce(dt_fim_horario::text, '') = '' 
and	(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '') 
and	exists (SELECT	1 
		from	prescr_mat_alteracao 
		where	nr_prescricao	= nr_prescricao_p 
		and	nr_seq_procedimento = nr_seq_procedimento_p 
		and	ie_alteracao = 35);
			

BEGIN 
 
if (ie_opcao_p	= 1) then 
 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_horario_w, 
		dt_horario_w, 
		cd_procedimento_w, 
		nr_seq_proc_interno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
 
		UPDATE	prescr_proc_hor 
		SET	dt_suspensao		= clock_timestamp(), 
			nm_usuario_susp		= nm_usuario_p 
		WHERE	nr_sequencia		= nr_seq_horario_w 
		AND	coalesce(dt_suspensao::text, '') = '' 
		AND	coalesce(dt_fim_horario::text, '') = '';
 
		SELECT	nextval('prescr_mat_alteracao_seq') 
		INTO STRICT	nr_seq_alteracao_w 
		;
 
		INSERT	INTO	prescr_mat_alteracao(nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_prescricao, 
			nr_seq_procedimento, 
			nr_seq_horario_proc, 
			dt_alteracao, 
			cd_pessoa_fisica, 
			ie_alteracao, 
			ie_tipo_item, 
			dt_horario, 
			nr_atendimento, 
			cd_item, 
			nr_seq_proc_interno) 
		VALUES ( 
			nr_seq_alteracao_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_prescricao_p, 
			nr_seq_procedimento_p, 
			nr_seq_horario_w, 
			dt_horario_w, 
			obter_dados_usuario_opcao(nm_usuario_p,'C'), 
			35, 
			'P', 
			dt_horario_w, 
			nr_atendimento_p, 
			cd_procedimento_w, 
			nr_seq_proc_interno_w);
 
	end loop;
	close C01;
	 
else 
	open c02;
	loop 
	fetch c02 into	 
		nr_seq_horario_w, 
		dt_horario_w, 
		cd_procedimento_w, 
		nr_seq_proc_interno_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
 
		UPDATE	prescr_proc_hor 
		SET	dt_suspensao		 = NULL, 
			nm_usuario_susp		 = NULL 
		WHERE	nr_sequencia		= nr_seq_horario_w 
		AND	(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '') 
		AND	coalesce(dt_fim_horario::text, '') = '';
 
		SELECT	nextval('prescr_mat_alteracao_seq') 
		INTO STRICT	nr_seq_alteracao_w 
		;
 
		INSERT	INTO	prescr_mat_alteracao(nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_prescricao, 
			nr_seq_procedimento, 
			nr_seq_horario_proc, 
			dt_alteracao, 
			cd_pessoa_fisica, 
			ie_alteracao, 
			ie_tipo_item, 
			dt_horario, 
			nr_atendimento, 
			cd_item, 
			nr_seq_proc_interno) 
		VALUES ( 
			nr_seq_alteracao_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_prescricao_p, 
			nr_seq_procedimento_p, 
			nr_seq_horario_w, 
			dt_horario_w, 
			obter_dados_usuario_opcao(nm_usuario_p,'C'), 
			36, 
			'P', 
			dt_horario_w, 
			nr_atendimento_p, 
			cd_procedimento_w, 
			nr_seq_proc_interno_w);
 
	end loop;
	close C02;
end if;
 
commit;
 
end;
$body$
