SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baixar_item_req_motivo_pda (nr_requisicao_p bigint, nr_sequencia_p bigint, qt_baixar_p bigint, nr_seq_lote_fornec_p bigint, cd_motivo_baixa_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_material_w			double precision;
qt_conv_estoque_consumo_w	double precision;
cd_pessoa_atend_w		varchar(10);
nr_sequencia_w			integer;


BEGIN

select	coalesce(max(qt_material_requisitada),0),
	coalesce(max(b.qt_conv_estoque_consumo),1)
into STRICT	qt_material_w,
	qt_conv_estoque_consumo_w
from	material b,
	item_requisicao_material a
where	nr_requisicao = nr_requisicao_p
and	nr_sequencia  = nr_sequencia_p
and	a.cd_material = b.cd_material;

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_atend_w
from	usuario
where	nm_usuario = nm_usuario_p;

if (qt_material_w > 0) and (qt_material_w >= qt_baixar_p) then
	if (qt_material_w = qt_baixar_p) then
		update	item_requisicao_material
		set	nr_seq_lote_fornec = nr_seq_lote_fornec_p
		where	nr_requisicao = nr_requisicao_p
		and	nr_sequencia  = nr_sequencia_p;
	else
		select	max(nr_sequencia) + 1
		into STRICT	nr_sequencia_w
		from	item_requisicao_material
		where	nr_requisicao = nr_requisicao_p;

		if (coalesce(qt_baixar_p,0) = 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(183880, 'NR_REQUISICAO=' || nr_requisicao_p || ';' ||
									'NR_SEQUENCIA=' || nr_sequencia_p);
			/*ra.ise_application_error(-20011,'Erro ao desdobrar o item da requisição: ' || nr_requisicao_p || '/' || nr_sequencia_p || chr(13) ||
							'A quantidade a desdobrar é igual a zero!');*/
		end if;
		insert into item_requisicao_material(
			nr_requisicao,
			nr_sequencia,
			cd_estabelecimento,
			cd_material,
			qt_material_requisitada,
 			qt_material_atendida,
 			vl_material,
			dt_atualizacao,
			nm_usuario,
			cd_unidade_medida,
			dt_atendimento,
			cd_pessoa_recebe,
			cd_pessoa_atende,
			ie_acao,
			cd_motivo_baixa,
			qt_estoque,
			cd_unidade_medida_estoque,
			cd_conta_contabil,
			cd_material_req,
			nr_seq_lote_fornec,
			cd_cgc_fornecedor,
			ds_observacao,
			ie_geracao)
		SELECT	nr_requisicao,
			nr_sequencia_w,
			cd_estabelecimento,
			cd_material,
			qt_baixar_p, /*Quantidade requisitada, sendo gerada */
			qt_baixar_p, /*Quantidade atendida*/
			vl_material,
			clock_timestamp(),
			nm_usuario_p,
			cd_unidade_medida,
			clock_timestamp(),
			cd_pessoa_recebe,
			cd_pessoa_atend_w,
			'1',
			cd_motivo_baixa_p, /* Motivo da baixa que será considerada no item */
			obter_quantidade_convertida(cd_material, qt_baixar_p, cd_unidade_medida, 'UME'),
			cd_unidade_medida_estoque,
			cd_conta_contabil,
			cd_material_req,
			nr_seq_lote_fornec_p,
			cd_cgc_fornecedor,
			ds_observacao,
			'S'
		from	item_requisicao_material
		where	nr_requisicao = nr_requisicao_p
		and	nr_sequencia  = nr_sequencia_p;


		update item_requisicao_material
		set	qt_material_requisitada	= (qt_material_requisitada - qt_baixar_p),
			qt_estoque		= obter_quantidade_convertida(cd_material, (qt_material_requisitada - qt_baixar_p), cd_unidade_medida, 'UME')
		where	nr_requisicao		= nr_requisicao_p
		and	nr_sequencia		= nr_sequencia_p;


	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baixar_item_req_motivo_pda (nr_requisicao_p bigint, nr_sequencia_p bigint, qt_baixar_p bigint, nr_seq_lote_fornec_p bigint, cd_motivo_baixa_p bigint, nm_usuario_p text) FROM PUBLIC;

