CREATE OR REPLACE PROCEDURE baixar_itens_nao_dispensaveis ( nr_prescricao_p bigint, nr_seq_atepacu_p bigint, cd_motivo_baixa_p bigint, nm_usuario_p text ) AS $body$
DECLARE

 
nr_seq_prescr_w			bigint;
nr_seq_mat_atend_w		bigint;
nr_atendimento_w		bigint;
cd_setor_atendimento_w		integer;
cd_material_w			integer;
dt_entrada_unidade_w		timestamp;
cd_unidade_medida_w		varchar(30);
qt_material_w			double precision;
cd_convenio_w			integer;
cd_categoria_w			varchar(10);
nr_doc_convenio_w		varchar(20);
ie_tipo_guia_w			varchar(2);
dt_prescricao_w			timestamp;
ie_via_aplicacao_w		varchar(5);
cd_fornec_consignado_w		varchar(14);
nr_seq_lote_fornec_w		bigint;
cd_senha_w			varchar(20);

c01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		c.nr_atendimento, 
		a.cd_material, 
		a.cd_unidade_medida,	 
		a.qt_material, 
		c.dt_prescricao, 
		a.ie_via_aplicacao, 
		a.cd_fornec_consignado, 
		a.nr_seq_lote_fornec 
	from	prescr_medica c, 
		prescr_material a 
	where	a.nr_prescricao		= c.nr_prescricao 
	and	a.nr_prescricao		= nr_prescricao_p 
	and	substr(obter_se_material_estoque(c.cd_estabelecimento, 0, a.cd_material),1,1) = 'N' 
	and	substr(obter_material_baixa_estoq_pac(c.cd_estabelecimento, 0, a.cd_material),1,1) = 'N' 
	and	coalesce(a.dt_baixa::text, '') = '' 
	and	not exists ( 
		SELECT	1 
		from	material_atend_paciente x 
		where	x.nr_prescricao			= a.nr_prescricao 
		and	x.nr_sequencia_prescricao	= a.nr_sequencia);

BEGIN
 
select	cd_setor_atendimento, 
	dt_entrada_unidade 
into STRICT	cd_setor_atendimento_w, 
	dt_entrada_unidade_w 
from	atend_paciente_unidade 
where	nr_seq_interno = nr_seq_atepacu_p;
 
OPEN	c01;
LOOP 
FETCH	c01 INTO 
	nr_seq_prescr_w, 
	nr_atendimento_w, 
	cd_material_w, 
	cd_unidade_medida_w, 
	qt_material_w, 
	dt_prescricao_w, 
	ie_via_aplicacao_w, 
	cd_fornec_consignado_w, 
	nr_seq_lote_fornec_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	BEGIN 
	SELECT * FROM obter_convenio_execucao(nr_atendimento_w, clock_timestamp(), cd_convenio_w, cd_categoria_w, nr_doc_convenio_w, ie_tipo_guia_w, cd_senha_w) INTO STRICT cd_convenio_w, cd_categoria_w, nr_doc_convenio_w, ie_tipo_guia_w, cd_senha_w;
 
	select	nextval('material_atend_paciente_seq') 
	into STRICT	nr_seq_mat_atend_w 
	;
 
	insert into material_atend_paciente( 
		nr_sequencia, 
		nr_atendimento, 
		dt_entrada_unidade, 
		cd_material, 
		dt_atendimento, 
		cd_unidade_medida, 
		qt_material, 
		dt_atualizacao, 
		nm_usuario, 
		cd_convenio, 
		cd_categoria, 
		nr_doc_convenio, 
		ie_tipo_guia, 
		dt_prescricao, 
		cd_material_prescricao, 
		cd_material_exec, 
		ie_via_aplicacao, 
		nr_prescricao, 
		nr_sequencia_prescricao, 
		cd_acao, 
		cd_setor_atendimento, 
		qt_executada, 
		vl_unitario, 
		qt_ajuste_conta, 
		ie_valor_informado, 
		ie_guia_informada, 
		ie_auditoria, 
		nm_usuario_original, 
		cd_situacao_glosa, 
		cd_cgc_fornecedor, 
		nr_seq_lote_fornec, 
		nr_seq_atepacu, 
		cd_senha) 
	values (	nr_seq_mat_atend_w, 
		nr_atendimento_w, 
		dt_entrada_unidade_w, 
		cd_material_w, 
		clock_timestamp(), 
		cd_unidade_medida_w, 
		qt_material_w,		 
		clock_timestamp(), 
		nm_usuario_p, 
		cd_convenio_w, 
		cd_categoria_w, 
		nr_doc_convenio_w, 
		ie_tipo_guia_w, 
		dt_prescricao_w, 
		cd_material_w, 
		cd_material_w, 
		ie_via_aplicacao_w, 
		nr_prescricao_p, 
		nr_seq_prescr_w, 
		'1', cd_setor_atendimento_w, 
		qt_material_w, 0, 0, 
		'N', 'N', 'N', 
		nm_usuario_p, 0, 
		cd_fornec_consignado_w,	 
		nr_seq_lote_fornec_w, 
		nr_seq_atepacu_p, 
		cd_senha_w);
 
	update	prescr_material 
	set	dt_baixa	= clock_timestamp(), 
		nm_usuario	= nm_usuario_p, 
		cd_motivo_baixa	= cd_motivo_baixa_p 
	where	nr_prescricao	= nr_prescricao_p 
	and	nr_sequencia	= nr_seq_prescr_w;
	end;
END LOOP;
CLOSE c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
