SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baixar_lote_prescr_prim_hor (dt_parametro_p timestamp) AS $body$
DECLARE


qt_horas_baixa_lote_w	bigint;


BEGIN

select	coalesce(max(qt_horas_baixa_lote),0)
into STRICT	qt_horas_baixa_lote_w
from	parametros_farmacia
where	cd_estabelecimento = coalesce(wheb_usuario_pck.get_cd_estabelecimento, cd_estabelecimento);

if (qt_horas_baixa_lote_w > 0) then

	update	ap_lote
	set	ie_status_lote	= 'C',
		dt_cancelamento	= clock_timestamp(),
		nm_usuario_cancelamento = 'JOB BLPPH_J'
	where	ie_status_lote	= 'G'
	and	(dt_prim_horario + (qt_horas_baixa_lote_w / 24)) < dt_parametro_p;

	insert into ap_lote_historico(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_lote,
		ds_evento,
		ds_log)
	SELECT	nextval('ap_lote_historico_seq'),
		clock_timestamp(),
		'JOB BLPPH_J',
		nr_sequencia,
		'Job: baixar_lote_prescr_prim_hor_j',
		'Cancelado pela Job: baixar_lote_prescr_prim_hor_j'
	from	ap_lote
	where	ie_status_lote	= 'G'
	and	(dt_prim_horario + (qt_horas_baixa_lote_w / 24)) < dt_parametro_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
