CREATE OR REPLACE PROCEDURE baixar_ordem_compra_transf ( nr_ordem_compra_p bigint, nr_seq_motivo_baixa_p bigint, ds_justificativa_baixa_p text, nm_usuario_p text) AS $body$
DECLARE

 
ds_historico_w		varchar(4000);
nr_seq_motivo_baixa_w	bigint := null;


BEGIN 
 
if (coalesce(nr_seq_motivo_baixa_p,0) > 0) then 
	nr_seq_motivo_baixa_w := nr_seq_motivo_baixa_p;
end if;
 
update	ordem_compra 
set	dt_baixa	= clock_timestamp(), 
	nr_seq_motivo_baixa = nr_seq_motivo_baixa_w, 
	nm_usuario	= nm_usuario_p, 
	dt_atualizacao	= clock_timestamp() 
where	nr_ordem_compra	= nr_ordem_compra_p;
 
update	processo_aprov_compra 
set	ie_aprov_reprov = 'B' 
where	nr_sequencia in ( 
	SELECT	distinct(nr_seq_aprovacao) 
	from	ordem_compra_item 
	where	nr_ordem_compra = nr_ordem_compra_p) 
and	ie_aprov_reprov = 'P';
 
if (coalesce(nr_seq_motivo_baixa_w,0) > 0) then 
	begin		 
	ds_historico_w := substr(WHEB_MENSAGEM_PCK.get_texto(297828,'DS_MOTIVO_W=' || substr(obter_motivo_cancel_oc(nr_seq_motivo_baixa_w),1,80) || ';' || 
								'DS_JUSTIFICATIVA_W=' || ds_justificativa_baixa_p),1,4000);
	end;
else 
	ds_historico_w := WHEB_MENSAGEM_PCK.get_texto(297830);
end if;
 
CALL inserir_historico_ordem_compra( 
	nr_ordem_compra_p, 
	'S', 
	WHEB_MENSAGEM_PCK.get_texto(297831), 
	ds_historico_w, 
	nm_usuario_p);
 
commit;
end;
$body$
